// import { response } from "express";
import { useEffect, useState } from "react";
import {Link} from "react-router-dom";

export default function Header() {
  const [username, setUsername] = useState(null);
  useEffect(() => {
    fetch('http://localhost:4000/profile', {
      credentials: 'include',  
    }).then(response => {
      response.json().then(userInfo => {  //parsing json here
        setUsername(userInfo.username); //if user exisits and credentials match then display username on the page.
      });  //we can do await or .then
    });
  }, []);


  function logout() {
    fetch('http://localhost:4000/logout', {
      credentials: 'include',
      method: 'POST',
    });
    // setUserInfo(null);
  }

  setUsername(null);

    return (
        <header>
        <Link to="/" className="logo">My Blog</Link>
        <nav>
          {username && (
            <>
              {/* if we have username then give link to cerare a new article. */}
              <Link to="/create">Create new post</Link>
              <a onClick={logout}>Logout ({username})</a>
            </>
          )}
          {!username && (
            <>
              {/* if we do not have username then give option to Login and register */}
              <Link to="/login" className="">Login</Link>
              <Link to="/register" className="">Register</Link>
            </>
          )}
          
        </nav>
      </header>
    );
}
